function updateTimer(){future=Date.parse("May 26, 2018 12:00:00"),now=new Date-36e5,diff=future-now,days=Math.floor(diff/864e5),hours=Math.floor(diff/36e5),mins=Math.floor(diff/6e4),secs=Math.floor(diff/1e3),d=days,h=hours-24*days,m=mins-60*hours,s=secs-60*mins,document.getElementById("timer").innerHTML='<div class="counter-block"><div class="counter-item"><span class="day">'+d+'</span><span class="unit">Dagen</span></div></div><div class="counter-block"><div class="counter-item"><span class="day">'+h+'</span><span class="unit">Uren</span></div></div><div class="counter-block"><div class="counter-item"><span class="day">'+m+'</span><span class="unit">Minuten</span></div></div><div class="counter-block"><div class="counter-item"><span class="day">'+s+'</span><span class="unit">Seconden</span></div></div>'}setInterval("updateTimer()",1e3);